import React, { Component } from 'react'

import Heading from '../components/heading';
import Button from '../components/button';

import '../assets/addCodePage.css';
import FileImage from '../assets/images/file-image-regular.svg';

class addCodePage extends Component {

    state = {
        selectedFile: null,
        uploaded: false,
        imgSrc: null
    }

    fileSelectedHandler = async (event) => {
        await this.setState({
            selectedFile: event.target.files[0],
            uploaded: true
        });
        this.fileDisplay();
    }

    fileDisplay = () => {
        var reader = new FileReader();
        var url = reader.readAsDataURL(this.state.selectedFile);
        reader.onloadend = () => {
            this.setState({
                imgSrc: [
                 reader.result   
                ]
            });
        console.log(url);
        }   
    }

    fileUploadHandler = () => {
        console.log("1:",this.state.selectedFile);
        console.log("2:",this.state.selectedFile.name);
        const fd = new FormData();
        fd.append('image',this.state.selectedFile,this.state.selectedFile.name);
        console.log(fd);
    }

    render() {
        return (
            <div>
                <Heading />
                <p className="paraStyle">Drop an image or click to browse</p>

                <input type="file" name="file" id="file" className="inputfile" onChange={this.fileSelectedHandler}/>
                {this.state.uploaded ? <label className="display-file-name">{this.state.selectedFile.name}</label> :<label htmlFor="file"><img src={FileImage} alt="fileimage"></img></label> } 
                <img src={this.state.imgSrc} alt="selectedImage" />

                <label className="labelStyle">Or enter the image URL: </label>
                <input className="inputStyle" type="text"></input>                

                <Button name="Crop Image"></Button>
                <Button name="Upload" click={this.fileUploadHandler}></Button>
            </div>
        )
    }
}

export default addCodePage
